mapper.select:
SELECT BOARD_NO, BOARD_TITLE, BOARD_WRITER, NICK_NAME, COUNT, B.CREATE_DATE, ( SELECT CHANGE_NAME FROM BOARD_IMG WHERE BOARD_NO = REF_BNO AND IMG_LEVEL = 0 ) THUMBNAIL FROM BOARD B JOIN MEMBER ON USER_NO = BOARD_WRITER WHERE B.STATUS = 'Y' AND BOARD_CD = #{boardCode}
AND BOARD_WRITER LIKE '%' || #{keyword} || '%' AND BOARD_TITLE LIKE '%' || #{keyword} || '%' AND BOARD_CONTENT LIKE '%' || #{keyword} || '%' AND (BOARD_TITLE LIKE '%' || #{keyword} || '%' OR BOARD_CONTENT LIKE '%' || #{keyword} || '%')
ORDER BY B.CREATE_DATE DESC

mapper.update:
UPDATE BOARD SET
COUNT = COUNT+1
WHERE BOARD_NO = #{boardNo}
